import {CroctProvider, Slot,useCroct} from '@croct/plug-react'
function OnBoardingPage(){
  const croct = useCroct();
  function getPersona(){
      croct.user.edit().set('persona', '')
  }
  return (
    <Slot id="home-banner">
      <button onclick={getPersona()}>Get Persona</button>
      {({title,subtitle,cta}) => (
        <div>
            <strong>{title}</strong>
            <p>{subtitle}</p>
            <a href={cta.link}>{cta.label}</a>
        </div>
        )}
</Slot>
  )
}
function App() {
  return (
    <CroctProvider appId="00000000-0000-0000-0000-000000000000"> 
    <div>
    <OnBoardingPage/>
    </div>
    </CroctProvider>
  );
}

export default App;
