import {Link} from 'react-router-dom'
import React, {useState} from 'react';
import { useCroct } from '@croct/plug-react';
function Inicio (){
   const [count, setCount] = useState(0);
     const croct = useCroct();
     croct.user.edit().add('custom.persona', 'none').save();
   if(count == 1){
      croct.user.edit().set('custom.persona', 'marketer').save()
      setCount(1)
   } else if(count == 0){
return (
  <div id="Page">
        <div id="titulo">
          <h1>Seja bem-vindo a Croct!</h1> 
        </div>
        <div id="introdution">
          <p>Dentre as opções abaixo, qual você mais se identifica?</p>
          <Link to="/developer">
             <button onClick={()=> setCount(count + 1)}>Developer</button>
          </Link>
          <Link to="/marketer">
             <button>Marketer</button>
          </Link>
          <Link to="/hacker">
             <button>Growth-Hackers</button>
          </Link>
    </div>
   </div>
)}
}
export default Inicio;
