import {CroctProvider, Slot} from '@croct/plug-react'
import {useCroct} from '@croct/plug-react'
function OnBoardingPage(props) {
  const croct = useCroct();
  croct.user.edit().set('custom.persona', 'developer').save();
  type HomeBannerContent = {
    title: string,
    subtitle: string,
    cta: {
        label: string,
        link: string,
    },
  };
  return (
    <Slot id="home-banner">
      <div>
                        <strong>{title}</strong>
                        <p>{subtitle}</p>
                        <a href={cta.link}>{cta.label}</a>
                    </div>
    </Slot>
  )
}
function App() {
  return (
    <CroctProvider appId="00000000-0000-0000-0000-000000000000"> 
    <div>
    <OnBoardingPage name="Mayara"/>
    </div>
    </CroctProvider>
  );
}

export default App;
