import React from "react";
import {useCroct, Slot} from '@croct/plug-react';
function Inicio(){
    type HomeBannerContent = {
        title: string,
        subtitle: string,
        cta: {
            label: string,
            link: string,
        },
      };
const croct = useCroct();
croct.user.edit().add('custom.persona', 'none').save();
function marketing(){
    croct.user.edit().set('custom.persona', 'marketer');
}
function developer()
    return (
    <div>
        <Slot id="home-banner">
                {({title, subtitle, cta}: HomeBannerContent) => (
                    <div>
                        <strong>{title}</strong>
                        <p>{subtitle}</p>
                        <a href={cta.link}>{cta.label}</a>
                    </div>
                )}
            </Slot>
          <div>
            <button onClick={marketing}>Marketing</button>
            <button onClick={developer}>Developer</button>
            <button onClick={hacker}>Hacker</button>
            </div>  

    </div>
    )
}
export default Inicio;
