import {CroctProvider, Slot,useCroct} from '@croct/plug-react'
function OnBoardingPage(){
  const croct = useCroct();
  function getPersona(){
      croct.user.edit().set('custom.persona', 'developer')
  }
  return (
    <div>
      <button onClick={getPersona()}>Developer</button>
      <Slot id="home-banner">
            {({title, subtitle}) => useContent(
                <div>
                    <h1>{title}</h1>
                    <p>{subtitle}</p>
                </div>
            )}
        </Slot>
        </div>
  )
}
function App() {
  return (
    <CroctProvider appId="00000000-0000-0000-0000-000000000000"> 
    <div>
    <OnBoardingPage/>
    </div>
    </CroctProvider>
  );
}

export default App;
