import {Link} from 'react-router-dom'
import React, {useState} from 'react';
import { useCroct } from '@croct/plug-react';
function Inicio (){
   type HomeBannerContent = {
      title: string,
      subtitle: string,
      cta: {
          label: string,
          link: string,
      },
    };
     const croct = useCroct();
     croct.user.edit().add('custom.persona', 'none').save();
     function Developer(){
      croct.user.edit().set('custom.persona', 'developer').save();
     }
return (
  <div id="Page">
        <div id="titulo">
          <h1>Seja bem-vindo a Croct!</h1> 
        </div>
        <div id="introdution">
          <p>Dentre as opções abaixo, qual você mais se identifica?</p>
          <Link to="/developer">
             <button onClick={Developer}>Developer</button>
          </Link>
          <Link to="/marketer">
             <button>Marketer</button>
          </Link>
          <Link to="/hacker">
             <button>Growth-Hackers</button>
          </Link>
    </div>
      <div>
         <Slot id="home-banner">
       {({title, subtitle, cta}: HomeBannerContent)  => (
      <div>
        <strong>{title}</strong>
        <p>{subtitle}</p>
        <a href={cta.link}>{cta.label}</a>
      </div>
       )}
    </Slot>
        <Link to="/">
        <button>Voltar</button></Link>
        </div>
   </div>
)}
export default Inicio;
