import {Link} from 'react-router-dom'
import React, {useState} from 'react';
import { useCroct, Slot} from '@croct/plug-react';
function Inicio (){
   type HomeBannerContent = {
      title: string,
      subtitle: string,
      cta: {
          label: string,
          link: string,
      },
    };
     const croct = useCroct();
     croct.user.edit().add('custom.persona', 'none').save();
     function Developer(){
      croct.user.edit().set('custom.persona', 'developer').save();
     }
     function Marketer (){
      croct.user.edit().set('custom.persona', 'marketer').save();
     }
     
return (
  <div id="Page">
        <div id="titulo">
          <h1>Seja bem-vindo a Croct!</h1> 
        </div>
        <div id="introdution">
          <p>Dentre as opções abaixo, qual você mais se identifica?</p>
             <button onClick={Developer}>Developer</button>
             <button onClick={Marketer}>Marketing</button>
             <button onClick={hacker}>Hacker</button>
        </div>
      <div>
         <Slot id="home-banner">
       {({title, subtitle, cta}: HomeBannerContent)  => (
      <div>
        <strong>{title}</strong>
        <p>{subtitle}</p>
        <a href={cta.link}>{cta.label}</a>
      </div>
       )}
        </Slot>
     </div>
   </div>
)}
export default Inicio;
